  line-height: 48px;
}
article.smaller q::before,
article.smaller q::after {
  font-size: 60px;
}
.build > * {
  transition: opacity 0.2s ease-in-out 0.2s;
}
.to-build {
  opacity: 0;
}
.twitter-tweet {
  letter-spacing: 0;
  transform: scale(3.2) !important;
}
html {
  box-sizing: border-box;
  background: #000 url("https://syntax.fm/static/background.jpg");
}
*,
*:before,
*:after {
  box-sizing: inherit;
}
h1,
h2,
h3,
h4,
h5,
h6 {
  color: #e1ab3a;
  font-weight: 900;
  line-height: auto;
  text-shadow: 5px 5px 0 #000;
}
p,
a,
li {
  text-shadow: 4px 3px 0 rgba(0,0,0,0.3);
  margin: 15px 0;
}
p,
li,
small {
  color: #fff;
  font-weight: 200;
  text-transform: uppercase;
  font-size: 80px;
  letter-spacing: 1px;
  line-height: 1.2;
}
.sub {
  transform: skew(0, -4deg);
  margin-top: 10px;
  font-weight: 200;
  color: #fff !important;
  font-size: 60px;
}
h2 + .sub {
  transform: none;
}
small {
  font-size: 25px;
}
h1 {
  transform: skew(0, -4deg) rotateY(20deg);
  font-size: 200px;
  color: #e1ab3a;
  letter-spacing: -2px;
  line-height: 1;
}
h1 a {
  text-decoration: none;
  font-size: 20px;
  color: #000;
  padding-left: 10px;
}
ul {
  text-align: left;
  margin-left: 100px;
}
h2 {
  font-size: 100px;
  line-height: 1.2;
  margin: 0;
  font-weight: 900;
}
h2.title {
  font-size: 70px;
}
h2 a {
  color: #e1ab3a;
}
h2.skew {
  transform: skew(0, -4deg);
  display: inline-block;
}
h2 + img {
  margin-top: 20px !important;
}
.large {
  font-size: 200px;
}
.big {
  font-size: 250px;
}
.medium {
  font-size: 150px;
}
.huge {
  font-size: 450px;
}
h3 {
  font-size: 70px;
  color: #e1ab3a;
  line-height: 1.5;
}
h4 {
  color: #e1ab3a;
  font-size: 50px;
}
.halves {
  width: 100%;
}
.half {
  width: 50%;
}
img.wide {
  width: 100%;
  height: auto;
  max-height: none;
}
img.high {
  width: auto;
  height: 100%;
  max-height: 1200px;
}
video.high {
  width: auto;
  height: 100%;
  min-width: 0;
}
.hl {
  color: #e1ab3a;
}
mark {
  background: #e1ab3a;
  color: #000;
  color: var(--black);
  text-shadow: none;
  padding: 0 10px;
  color: #000;
}
.emoji {
  font-size: 116px;
  font-family: serif;
}
body.square .slides > article {
  width: 1920px;
  height: 1080px;
  margin-left: -960px;
  margin-top: -540px;
  border-radius: 0;
}
body.square .slides > article h2 {
  font-size: 110px;
}
body.square .slides > article p,
body.square .slides > article li,
body.square .slides > article small {
  font-size: 30px;
}
body.square .slides > article .big {
  font-size: 180px;
}
body.square .slides > article .large {
  font-size: 140px;
}
body.square .slides > article .huge {
  font-size: 220px;
}
.itm {
  background: var(--yellow);
  color: #000;
  padding: 15px;
}
.alignment {
  height: 700px;
  width: 700px;
  border: 10px solid #f00;
}
pre {
  text-align: left;
  padding: 50px;
  font-family: 'Operator Mono', 'Dank Mono', 'Courier New', Courier, monospace;
  font-weight: 300;
  font-size: 30px;
  line-height: 1.2;
}
